mapper.insert:
insert into TASK_OUTPUT values(
'to' || LPAD(to_seq.nextval, 5,'0'), #{toFile}, 
	#{toWriter}, sysdate, #{ptId}
)

mapper.update:
update PRJ_TASK
	set PT_TITLE =  #{ptTitle}
		,PT_CONTENT = #{ptContent}
		,PT_STARTDATE = #{ptStartdate}
		,PT_DUEDATE = #{ptDuedate}
		,PT_CHARGE = #{ptCharge}
		,PT_TYPE = #{ptType}
		,PT_STATUS = #{ptStatus}
where PT_ID = #{ptId}
