mapper.select:
select count(*)
from prj_task
where 1=1
and pi_id = #{piId}

and pt_title like '%'||#{ptTitle}||'%'


and pt_charge like '%'||#{ptCharge}||'%'



	and pt_startdate between #{ptStartdate} and #{ptDuedate}
	and pt_duedate between #{ptStartdate} and #{ptDuedate}


	and pt_startdate >= #{ptStartdate}


	and pt_duedate <= #{ptDuedate}

select *
from (
	SELECT row_number() over(ORDER BY to_date(pt_startdate, 'YYYY-MM-DD')) cnt, prj_task.*, user_info.*
	from prj_task, user_info
	where 1=1
	and pi_id = #{piId}

	and pt_title like '%'||#{ptTitle}||'%'


	and pt_charge like '%'||#{ptCharge}||'%'



		and pt_startdate between #{ptStartdate} and #{ptDuedate}
		and pt_duedate between #{ptStartdate} and #{ptDuedate}


		and pt_startdate >= #{ptStartdate}


		and pt_duedate <= #{ptDuedate}


)
where cnt between #{firstPage} and #{lastPage}
