mapper.update:
UPDATE user_info 
	SET UI_PW = #{uiPw}
		, UI_EMAIL = #{uiEmail}
		,UI_PHONE = #{uiPhone}
		, UI_RANK = #{uiRank}
		, UI_DEPT = #{uiDept}
	WHERE UI_ID = #{uiId}

mapper.select:
select *
from (
	SELECT row_number() over(ORDER BY to_date(nt_regdate, 'YYYY-MM-DD')) cnt, notice.*
	from notice
	where 1=1


	and nt_title like '%'||#{ntTitle}||'%'


	and nt_type like '%'||#{ntType}||'%'

)
where cnt between #{firstPage} and #{lastPage}
