mapper.select:
SELECT count(*)
FROM user_info
WHERE UI_ID = #{uiId}

SELECT * FROM user_info
WHERE UI_ID = #{uiId}
AND UI_PW = #{uiPw}

mapper.update:
UPDATE user_info 
	SET UI_PW = #{uiPw}
	, UI_EMAIL = #{uiEmail}
	, UI_PHONE = #{uiPhone}
	, UI_BIRTH = #{uiBirth}
	, UI_PIC = #{uiPic}
	, UI_INTRO = #{uiIntro}
	, UI_RANK = #{uiRank}
	, UI_DEPT = #{uiDept}
	, UI_STATUS = #{uiStatus}
	WHERE UI_ID = #{uiId}

mapper.delete:
delete from user_info
where UI_ID = #{uiId}
and UI_PW = #{uiPw}

mapper.select:
SELECT UI_ID FROM USER_INFO ui
WHERE UI_NAME = #{uiName}
AND UI_EMAIL = #{uiEmail}

SELECT UI_PW FROM USER_INFO ui
WHERE UI_NAME = #{uiName}
AND UI_ID = #{uiId}

mapper.update:
update USER_INFO
	set UI_PW = #{uiPw}
	where UI_NAME = #{uiName}
	and UI_ID = #{uiId}

mapper.select:
SELECT UI_EMAIL FROM USER_INFO ui
WHERE UI_NAME = #{uiName}
AND UI_ID = #{uiId}
