mapper.select:
SELECT count(*)
FROM user_info
WHERE UI_ID = #{uiId}

SELECT * FROM user_info
WHERE UI_ID = #{uiId}
AND UI_PW = #{uiPw}

mapper.update:
UPDATE user_info 
	SET UI_PW = #{uiPw}
	and UI_EMAIL = #{uiEmail}
	and UI_PHONE = #{uiPhone}
	and UI_BIRTH = #{uiBirth}
	and UI_PIC = #{uiPic}
	and UI_INTRO = #{uiIntro}
	and UI_RANK = #{uiRank}
	and UI_DEPT = #{uiDept}
	and UI_STATUS = #{uiStatus}
	WHERE UI_ID = #{uiId}

mapper.delete:
delete from user_info
where UI_ID = #{uiId}
and UI_PW = #{uiPw}
