mapper.select:
SELECT count(*)
FROM user_info
WHERE UI_ID = #{uiId}

SELECT * FROM user_info
WHERE UI_ID = #{uiId}
AND UI_PW = #{uiPw}

mapper.update:
UPDATE user_info 
	SET UI_PW = #{uiPw}
	, UI_EMAIL = #{uiEmail}
	, UI_PHONE = #{uiPhone}
	, UI_BIRTH = #{uiBirth}
	, UI_PIC = #{uiPic}
	, UI_INTRO = #{uiIntro}
	, UI_RANK = #{uiRank}
	, UI_DEPT = #{uiDept}
	, UI_STATUS = #{uiStatus}
	WHERE UI_ID = #{uiId}

mapper.delete:
delete from user_info
where UI_ID = #{uiId}
and UI_PW = #{uiPw}
